<?phpuse Alura\Doctrine\Entity\Aluno;use Alura\Doctrine\Entity\Telefone;use Alura\Doctrine\Helper\EntityManagerFactory;use Doctrine\DBAL\Logging\DebugStack;require_once __DIR__ . "/../vendor/autoload.php";$entityManagerFactory = new EntityManagerFactory();$entityManager = $entityManagerFactory->getEntityManager();$alunosRepository = $entityManager->getRepository(Aluno::class);$debugStack = new DebugStack();$entityManager->getConfiguration()->setSQLLogger($debugStack);$classeAluno = Aluno::class;$dql = "SELECT a, t, c FROM $classeAluno a JOIN a.telefones t JOIN a.cursos c";$query = $entityManager->createQuery($dql);$alunos = $query->getResult();foreach ($alunos as $aluno) {    $telefones = $aluno        ->getTelefones()        ->map(function (Telefone $telefone) {            return $telefone->getNumero();        })        ->toArray();    echo "id: {$aluno->getId()}" . PHP_EOL;    echo "Nome: {$aluno->getNome()}" . PHP_EOL;    echo "Telefones: " . implode(", ", $telefones) . PHP_EOL;    $cursos = $aluno->getCursos();    foreach ($cursos as $curso) {        echo "\tid: {$curso->getId()} \n";        echo "\tCurso: {$curso->getNome()} \n";        echo PHP_EOL;    }    echo PHP_EOL;}foreach ($debugStack->queries as $queryInfo) {    echo $queryInfo['sql'] . PHP_EOL;}